#
#These login tests must be run after the register tests
#
require 'rubygems'
require 'selenium-webdriver'
require 'pry'

port = 8081

driver = Selenium::WebDriver.for :firefox
driver.get "localhost:#{port}"

##################### Try logging in without input ####################
button = driver.find_element(:tag_name, "button")
button.click

if driver.current_url == "http://localhost:#{port}/\#/login"
  puts "Passed - Did not login without input"
else
  puts "FAILED - Logged in without input"
end

##################### Try logging in with invalid username/password ####################
form = driver.find_elements(:class, "form-control")
username = form[0]
password = form[1]

userIn = "notAUsername"
passIn = "NOTaPassword"

username.send_keys userIn
password.send_keys passIn

button.click

#These are place holders for when the real server messages come back
driver.switch_to.alert.accept rescue Selenium::WebDriver::Error::NoAlertOpenError
driver.switch_to.alert.accept rescue Selenium::WebDriver::Error::NoAlertOpenError


if driver.current_url == "http://localhost:#{port}/\#/login"
  puts "Passed - Did not login with invalid credentials"
else
  puts "FAILED - Logged in without valid input"
end

##################### Try putting in a TON of text into text fields without newlines ####################
username.clear
password.clear

userIn = ""
passIn = "pass1"
username.send_keys userIn
password.send_keys passIn

if username["value"].length == 25 && password["value"].length == passIn.length
  puts "Passed - Prevented input of more than 25 characters"
else
  puts "FAILED - Allowed input with more than 25 characters"
end

##################### Try putting in text with newlines into text fields ####################
username.clear
password.clear

username.send_keys "hello from the other side
I must have called a thousand times
To tell you I'm sorry
For everything that I've done"
#These are place holders so that the test can keep running
driver.switch_to.alert.accept rescue Selenium::WebDriver::Error::NoAlertOpenError
driver.switch_to.alert.accept rescue Selenium::WebDriver::Error::NoAlertOpenError
driver.switch_to.alert.accept rescue Selenium::WebDriver::Error::NoAlertOpenError
driver.switch_to.alert.accept rescue Selenium::WebDriver::Error::NoAlertOpenError

password.send_keys "Adele"

##################### Try logging in with good username/password ####################
username.clear
password.clear

username.send_keys "user1"
password.send_keys "password"

button.click

#These are place holders for when the real server messages come back
#The user should be taken to the Game Lobby page at this point
driver.switch_to.alert.accept rescue Selenium::WebDriver::Error::NoAlertOpenError
driver.switch_to.alert.accept rescue Selenium::WebDriver::Error::NoAlertOpenError

if driver.current_url == "http://localhost:#{port}/\#/lobby"
  puts "Passed - Logged with valid credentials"
else
  puts "FAILED - Log in failed in with valid input"
end

driver.quit
